css:
.print-format {
    padding:0 !important;
    margin: 0px 0px 0px 0px !important;
    width: 813px !important;
	height: 1058px !important;
	font-size: 10pt !important;
}
.print-format th {background-color: transparent !important;}
.print-format td {padding: 0px !important;}
@media print {
    @page{
        size: 20cm 28cm !important;
    }
	body {
    width: 813px !important;
	height: 1058px !important;
	background: #fff;
	margin: 0 !important;
	padding: 0 !important;
	font-size: 10pt !important;
	color: #000;
	}
}

@media screen,handheld,projection,tv {
	body{
		margin: 0px 0px 0px 0px;
		color : #000000;
		background-color: #ffffff;
		font-family: Arial;
		font-size: 12px;
	}
}

.barcode-label{
    height: 29mm;
    width: 50mm;
}
.barcode-label {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr;
  grid-template-rows: 1fr 1fr 1fr;
  gap: 0px 0px;
  justify-items: center;
    align-items: center;
  grid-template-areas:
    "label-item-name label-item-name label-item-name label-item-name"
    "label-item-price label-item-price label-item-price label-item-price"
    "label-item-code label-item-barcode label-item-barcode label-item-barcode";

}

.label-item-name { 
    grid-area: label-item-name;
    font-size: 3mm;
    width: 50mm;
    height: 10mm;
    line-height: 3.5mm;
    z-index: 1;
    overflow: hidden;
}

.label-item-price { 
    grid-area: label-item-price;
    font-size: 2em;
    font-weight: 600;
    width: 50mm;
    height: 8mm;
}

.label-item-code { 
    grid-area: label-item-code;
    width: 15mm;
    height: 11mm;
    font-size: 2.5mm;
    overflow-wrap: break-word;


}

.label-item-code-alone { 
    grid-area: label-item-code;
    width: 25mm;
    height: 11mm;
    font-size: 2.5mm;
    overflow-wrap: break-word;
}

.label-item-barcode { 
    grid-area: label-item-barcode;
    width: 35mm !important;
    height: 12mm;
    font-size: 0.5em;

}
.barcode-container{
    max-width: 100%;
    max-height: 100%;

}
.barcode-label-container {
border: 1px solid #d1d8dd;
width: fit-content;
overflow: hidden;
}
.print-format, .barcode-label-container, .dashboard-section{
margin: 0;
padding: 0;
}
.label-field{

    padding: 1mm;
    //border: 1px solid black;
    text-align: center;

}
